#root.root {--general-border-radius: 5px;}
/* ============ Define all variable ============ */
#root.root {

    --tabs-indent: 18px;
    --tabs-inner-gap: 6px;
    --tabs-pinned-height: 40px;
    --tabs-pinned-width: 50px;
    --general-margin: 5px;
    --tabs-height: 32px;
    --history-item-padding-left: 6px;
    --history-item-margin: 5px;
    --history-item-border-radius: 8px;
    --tabs-close-btn-margin: 3px;
    --nav-btn-margin: 0px 5px;
    --nav-btn-width: 28px;
    --nav-btn-height: 28px;
    --nav-btn-len-margin: -1px;
    --tabs-activated-shadow: inset 0 0 0 1px light-dark(rgba(0, 0, 0, 0.3), rgba(255, 255, 255, 0.3));
    --ntb-btn-height: 28px;
    --tabs-lvl-opacity: 0.1;
}

/* ============ gives the Pinned Tabs their border radius ============ */
#root.root .PinnedTabsBar .Tab {
		border-radius: 5px;																			
}


/* ============ New Tab button ============ */
/* Adjust padding and margin for new tab buttons */
.new-tab-btns { margin-top: 8px !important; }



/* === Makes the black containers transparent === */
.TabsPanel [data-color="toolbar"] {--color: ;}
.Tab[data-pin="true"] .ctx {box-shadow: 0 0 1px 0 rgba(255, 255, 255, 0.0);}


/* === gives the favicons drop shadow for better visability === */
.Tab .fav-icon { filter: drop-shadow(1px 1px 1px rgba(150, 150, 150, 0.6)) !important;}

/* === gives the Tabs different Backgrounds depending on dark or light mode === */
@media (prefers-color-scheme: light) {
    .PinnedTabsBar .Tab {
        background: rgba(255, 255, 255, 1) !important;
    }
.Tab .body {
        background: rgba(255, 255, 255, 0.4);
    }
}
@media (prefers-color-scheme: dark) {
    .PinnedTabsBar .Tab {
        background: rgba(130, 130, 130, 0.1) !important;
    }
.Tab .body {
        background: rgba(130, 130, 130, 0.05);
    }
}


/* === Makes the unloaded tabs a little bit more visable === */
.Tab[data-discarded=true]>.body>.fav { opacity:0.7}
